import React from 'react'
import {Button} from '../Button'
import {useState} from "react"



function Login() {

    const [email, setEmail] = useState('');
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [tel, setTel] = useState('');
    const [errorNom, setNomError] = useState('');
    const [errorPrenom, setPrenomError] = useState('');
    const [errorTel, setErrorTel] = useState('');
    const [errorEmail, setErrorEmail] = useState('');





    const handleSubmit = (e) =>{
        e.preventDefault()

        if(!nom){
            setNomError("Le nom est vide, veuillez insérez un nom S'il vous plait")
        }else{
            setNomError("")
        }

        if(!prenom){
            setPrenomError("Le prénom est vide, veuillez insérez un prenom S'il vous plait")
        }else{
            setPrenomError("")
        }

        if(!email){
            setErrorEmail("L'email est vide, veuillez insérez un email S'il vous plait")
        }else{
            ssetErrorEmail("")
        }

        if(!email){
            setErrorTel("L'emplacement est vide, veuillez insérez un numérp S'il vous plait")
        }else{
            setErrorTel("")
        }




    }
  return (
    <>
        <h1>Login</h1>

        <form className="contactForm">
            <label>Nom</label>
            <input type='text' id="nom" name="nom" value={nom} onChange={(e) => setNom(e.target.value)}></input>

            <label>Prénom</label>
            <input type='text' id="prenom" name="prenom" value={prenom} onChange={(e) => setPrenom(e.target.value)}></input>

            <label>Tél:</label>
            <input type='telephone' id="telephone" name="telephone" value={tel} onChange={(e) => setTel(e.target.value)}></input>

            <label>email:</label>
            <input type='email' id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)}></input>

            <button type="submit" onClick={handleSubmit}>Submit</button>



        </form>

    </>
  )
}

export default Login
